var url="https://checkpointlive.com/app/api/event/tracking",getPosition=function(){return new Promise(function(t,e){navigator.geolocation.getCurrentPosition(t,e,{enableHighAccuracy:!0})})};function lost(){var t=document.getElementById("teamNumber").value;Promise.resolve().then(function(){if(""===t)throw"Please enter a team number"}).then(function(){return getPosition()}).then(function(e){var n={trackingUrl:"http://vps358200.ovh.net:5055",id:"Patrol-"+t,lat:e.coords.latitude,lon:e.coords.longitude,accuracy:e.coords.accuracy,loggedBy:"Patrol "+t,offRoute:"lost"},o=Object.entries(n).map(([t,e])=>`${t}=${e}`).join("&");return fetch(url,{method:"POST",headers:{"Content-type":"application/x-www-form-urlencoded; charset=UTF-8"},body:o})}).then(function(){alert("Your location and team number has been sent to event organisers")}).catch(function(t){alert(t)})}